// youtube tag
(function (d) {
 var s, v, i, l = d.getElementsByClassName("youtube"), y=0;
 if (l.length) {
  s = d.createElement("STYLE");
  s.type="text/css";
  s.innerHTML='.youtube{background-color:#000;max-width:100%;overflow:hidden;position:relative;cursor:hand;cursor:pointer}.youtube img{bottom:0;display:block;left:0;margin:auto;max-width:100%;position:absolute;right:0;top:0;width:100%;height:auto}.youtube div{filter:alpha(opacity=80);opacity:.8;height:77px;left:50%;margin-left:-38px;margin-top:-38px;position:absolute;top:50%;width:77px;background:url("data:image/png;base64,") no-repeat}';
  d.body.appendChild(s);
  while (v=l[y++]) {
   i = d.createElement("IMG");
   i.setAttribute("src", "https://i.ytimg.com/vi/"+v.id+"/hqdefault.jpg");
   v.appendChild(i);
   v.appendChild(d.createElement("DIV"));
   v.onclick = function() {
    var t=this, f=d.createElement("IFRAME");
    f.setAttribute("src", "https://www.youtube.com/embed/"+t.id+"?autoplay=1&autohide=1&rel=0&border=0&wmode=opaque&enablejsapi=1");
    f.style.width = t.style.width;
    f.style.height = t.style.height;
    f.style.border = "none";
    t.parentNode.replaceChild(f,t);
   }
  }
 }
})(document);


// [spoiler] tag
(function (d) {
 if ("classList" in d.documentElement){
  var el = d.getElementsByClassName("spoiler"), l=el.length, i, s;
  if (l) {
   s = d.createElement("STYLE");
   s.type="text/css";
   s.innerHTML='.unspoiled {color:#000; background:#000;}';
   d.body.appendChild(s);
   for (i=0; i<l; i++) {
    el[i].classList.add("unspoiled");
    el[i].addEventListener("click", function (e){e.target.classList.remove("unspoiled")}, false );
   }
  }
 }
})(document);


